import PropTypes from 'prop-types';
import React from 'react';
import {Col, Button, Form, FormGroup,Input} from 'reactstrap';
import { Field, reduxForm } from 'redux-form';


const validate = values => {
  const errors = {}
  if (!values.name) {
    errors.name = 'Required'
  } else if (values.name.length < 2) {
    errors.name = 'Minimum be 2 characters or more'
  }
  if (!values.roomno) {
    errors.roomno = 'Required'
  }
  if (!values.dob) {
    errors.dob = 'Required'
  }
  return errors
  }

  const renderField = ({ input,label, type, meta: { touched, error, warning } }) => (
      <div>
        <Input {...input} placeholder={label} type={type} className="form-control" />
        {touched && ((error && <span className="text-danger">{error}</span>) || (warning && <span>{warning}</span>))}
      </div>
  )


let RequestForm = props => {
    const {      
        // usernameLabel,
        handleSubmit,
        usernameInputProps,
        roomnoInputProps,
        dobInputProps
    } = props
    return(
            <Col className="form-main">
              <Form onSubmit={handleSubmit} >
                    <FormGroup>
                        {/* <Label for={usernameLabel}>{usernameLabel}</Label> */}
                        <Field {...usernameInputProps} className="input-bgColor" 
                         component={renderField} name="name" label="Name"/>
                    </FormGroup>
                    <FormGroup>

                        <Field {...roomnoInputProps} className="input-bgColor" 
                        component={renderField} name="roomno" label="Room No"/>
                    </FormGroup>
                    <FormGroup>

                        <Field {...dobInputProps} className="input-bgColor" 
                        component={renderField} name="dob" label="Date of Birth"/>
                    </FormGroup>


                    <Button
                        size="lg"
                        disabled={pristine || submitting} 
                        className="Requestbtn bg-gradient-Requestbtn btn-outline-info border-0"
                        block 
                        >
                            REQUEST ACCESS
                    </Button>

              </Form>
            </Col>

    );
  }

RequestForm.propTypes = {
    // usernameLabel: PropTypes.string,
    usernameInputProps: PropTypes.object,
    roomnoInputProps: PropTypes.object,
    dobInputProps: PropTypes.object
}

RequestForm.defaultProps = {
    usernameLabel: 'Bansal',
    usernameInputProps: {
      type: 'text',
      placeholder: 'Name',
    },
   roomnoLabel: 'Name',
   roomnoInputProps: {
      type: 'text',
      placeholder: 'Room No',
    },
   dobLabel: 'Date of Birth',
   dobInputProps: {
      type: 'text',
      placeholder: 'Date of Birth',
    },
};

RequestForm = reduxForm({
  form: 'request-form',
  validate,
})(RequestForm);

export default RequestForm;
