import { put, call, fork} from 'redux-saga/effects';
import * as actions from '../actions';

        export function * logIn(data) {
        yield put(actions.logIn.request());
        try {
                if (!data === "") {
                throw new Error({ error: { customMessage: 'fields are mandatory.' } });
                }
                const response = yield call('http://localhost:3000/', data);
                console.log(response);
        }
        catch ({ error }) {
        yield put(actions.logIn.failure(error));
        }
        }

        export function * watchLogIn() {
            while (true) {
            const { payload } = yield take(actions.GUEST_LOG_IN);
                    yield call(logIn, payload);
            }
            }


export default [
    fork(watchLogIn)
];
