const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});

export default function Header() {
  const classes = useStyles();
  const [state, setState] = React.useState({

    left: false,

  });

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [side]: open });
  };

  const sideList = side => (
  <>

        <div className="sidebarImg-main">
          <img
            src={componentImg}
            className="sidebarImg"
            alt="cmp"
          />
          <span className="headername text-white">
            Welcome Mr Bansal
          </span>

          <span className="crossbtn" onClick={toggleDrawer('left', false)}> <MdClose/></span>

  </div>

    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
    >
      <List>
        <Link to="/services">
        {['Service'].map((text) => (
          <ListItem button key={text}>
            <ListItemText primary={text} />
          </ListItem>
        ))}
        </Link>

        {/* <Link to="/services">
        {['Service'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
        </Link> */}

      </List>
      {/* <Divider /> */}
      <List>
      <Link to="/requestmain">
        {['My Request'].map((text) => (
          <ListItem button key={text}>
             <ListItemText primary={text} />
          </ListItem>
        ))}
        </Link>
      </List>
      <List>
      <Link to="/">
        {['Logout'].map((text) => (
          <ListItem button key={text}>
              <ListItemText primary={text} />
          </ListItem>
        ))}
        </Link>
      </List>
    </div>
    </>
  );

  return (
    <div>
      {/* <Button onClick={toggleDrawer('left', true)}></Button> */}

      <Navbar expand className="bgNav" ref={node => { this.node = node; }}>

         <Nav navbar className="">
         <IconButton
            // color="inherit"
            cursor= "pointer"
            aria-label="open drawer"
            onClick={toggleDrawer('left', true)}
            // edge="start"
            // className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>

         </Nav>
        <Nav className="Nav-Name">
          Welcome Mr.Bansal
        </Nav>


      <Nav navbar className='nav-right'>
        <NavItem className="d-inline-flex">
        <Link to="/checkout"><span> <FaShoppingCart size={25} style={{ color: '#fff' }} /></span></Link>         </NavItem>
      </Nav>

      </Navbar> 

      <Drawer open={state.left} onClose={toggleDrawer('left', false)}>
        {sideList('left')}
      </Drawer>
    </div>
  );
}

