import PropTypes from 'prop-types';
import React from 'react';
import {Col, Button, Form} from 'reactstrap';
import { Field, reduxForm } from 'redux-form';
import {TextInput}  from '../components/forms/TextInput';

const validate = values => {
  const errors = {}
  if (!values.name) {
    errors.name = 'Required'
  } else if (values.name.length < 3) {
    errors.name = 'Minimum be 3 characters or more'
  }
  if (!values.roomno) {
    errors.roomno = 'Required'
  }
  if (!values.dob) {
    errors.dob = 'Required'
  }
  return errors
  }

let RequestForm = props => {
    const {      
        // usernameLabel,
        handleSubmit, pristine, submitting,
        usernameInputProps,
        roomnoInputProps,
        dobInputProps
    } = props
    return(
            <Col className="form-main">
              <Form onSubmit={handleSubmit} >

                    <Field {...usernameInputProps} className="input-bgColor" 
                    component={TextInput} name="name" label="Name" />

                    <Field {...roomnoInputProps} className="input-bgColor" 
                    component={TextInput} name="roomno" label="Room No" />

                    <Field {...dobInputProps} type="date" className="input-bgColor" 
                    component={TextInput} name="dob" label="Date of Birth" />

                    <Button
                        size="lg"
                        type="submit"
                        disabled={pristine || submitting} 
                        className="Requestbtn bg-gradient-Requestbtn btn-outline-info border-0"
                        block 
                        >
                            REQUEST ACCESS
                    </Button>

              </Form>
            </Col>

    );
  }

RequestForm.propTypes = {
    // usernameLabel: PropTypes.string,
    usernameInputProps: PropTypes.object,
    roomnoInputProps: PropTypes.object,
    dobInputProps: PropTypes.object
}

RequestForm.defaultProps = {
    usernameLabel: 'Bansal',
    usernameInputProps: {
      type: 'text',
      placeholder: 'Name',
    },
   roomnoLabel: 'Name',
   roomnoInputProps: {
      type: 'text',
      placeholder: 'Room No',
    },
   dobLabel: 'Date of Birth',
   dobInputProps: {
      type: 'text',
      placeholder: 'Date of Birth',
    },
};

RequestForm = reduxForm({
  form: 'request-form',
  validate,
})(RequestForm);

export default RequestForm;
