import PropTypes from 'prop-types';
import React from 'react';
import {Col, Button, Form} from 'reactstrap';
import { Field, reduxForm } from 'redux-form';
import { TextInput } from '../components/forms/TextInput';
import {validate} from '../components/forms/validate';

// const validate = values => {
//   const errors = {}
//   if (!values.name) {
//     errors.name = 'Required'
//   } else if (values.name.length < 3) {
//     errors.name = 'Minimum be 3 characters or more'
//   }
//   if (!values.roomno) {
//     errors.roomno = 'Required'
//   }
//   if (!values.dob) {
//     errors.dob = 'Required'
//   }
//   return errors
//   }

  // const renderField = ({ input,label, type, meta: { touched, error, warning } }) => (
  //   <FormGroup>
  //       <Input {...input} placeholder={label} type={type}/>
  //       {touched && ((error && <span className="text-danger">{error}</span>) || (warning && <span>{warning}</span>))}
  //   </FormGroup>
  // )


let RequestForm = props => {
    const {      
        // usernameLabel,
        handleSubmit, pristine, submitting,
        usernameInputProps,
        roomnoInputProps,
        dobInputProps
    } = props
    return(
            <Col className="form-main">
              <Form onSubmit={handleSubmit} >

                    <Field {...usernameInputProps} className="input-bgColor" 
                    component={TextInput} name="name" label="Name" validate={[validate]}/>

                    <Field {...roomnoInputProps} className="input-bgColor" 
                    component={TextInput} name="roomno" label="Room No" validate={[validate]}/>

                    <Field {...dobInputProps} type="date" className="input-bgColor" 
                    component={TextInput} name="dob" label="Date of Birth" validate={[validate]}/>

                    <Button
                        size="lg"
                        type="submit"
                        disabled={pristine || submitting} 
                        className="Requestbtn bg-gradient-Requestbtn btn-outline-info border-0"
                        block 
                        >
                            REQUEST ACCESS
                    </Button>

              </Form>
            </Col>

    );
  }

RequestForm.propTypes = {
    // usernameLabel: PropTypes.string,
    usernameInputProps: PropTypes.object,
    roomnoInputProps: PropTypes.object,
    dobInputProps: PropTypes.object
}

RequestForm.defaultProps = {
    usernameLabel: 'Bansal',
    usernameInputProps: {
      type: 'text',
      placeholder: 'Name',
    },
   roomnoLabel: 'Name',
   roomnoInputProps: {
      type: 'text',
      placeholder: 'Room No',
    },
   dobLabel: 'Date of Birth',
   dobInputProps: {
      type: 'text',
      placeholder: 'Date of Birth',
    },
};

RequestForm = reduxForm({
  form: 'request-form',
  validate,
})(RequestForm);

export default RequestForm;
