import { put, call, fork,take} from 'redux-saga/effects';
import * as actions from '../actions';

        export function * guestLogIn(data) {
        yield put(actions.guestLogIn.request());
        try {
                if (!data === "") {
                throw new Error({ error: { customMessage: 'fields are mandatory.' } });
                }
                const response = yield call('http://localhost:3000/', data);
                console.log("response---------->",response);
                if (response.user.length > 0){
                    yield put(actions.initialize({ IS_LOGIN: true }));
                            yield put(actions.logIn.success(response));
                            yield call(history.push, '/dashboard');
                }
                else{
                    yield put(actions.logIn.failure({ customMessage:'Access Denied.'}));
                }

        }
        catch ({ error }) {
        yield put(actions.guestLogIn.failure(error));
        }
        }

        export function * watchLogIn() {
            while (true) {
            const { payload } = yield take(actions.GUEST_LOG_IN);
                    yield call(guestLogIn, payload);
            }
            }


export default [
    fork(watchLogIn)
];
