import PropTypes from 'prop-types';
import React from 'react';
import {Col, Button, Form} from 'reactstrap';
import { Field, reduxForm } from 'redux-form';
import TextInput  from '../components/forms/TextInput';
import {Link} from 'react-router-dom';
import DatePicker from './forms/DatePickerInput';


const validate = values => {
  const errors = {}
  if (!values.name) {
    errors.name = 'Required'
  } else if (!values.name.match(/^[a-zA-Z]*$/)) {
    errors.name = 'Please enter alphabet characters only.'
  } else if (values.name.length < 3) {
    errors.name = 'Minimum be 3 characters or more'
  }
  if (!values.roomno) {
    errors.roomno = 'Required'
  }
  if (!values.dob) {
    errors.dob = 'Required'
  }
  return errors
  }

let RequestForm = props => {
    const { handleSubmit, pristine, submitting } = props
    return(
            <Col className="form-main">
              <Form onSubmit={handleSubmit} >

                    <Field component={TextInput} name="name" label="Name" />

                    <Field component={TextInput} name="roomno" label="Room No" />

                    {/* <Field component={TextInput} name="dob" label="Date of Birth" /> */}
                    <Field
  name="birthday"
  inputValueFormat="YYYY-MM-DD"
  dateFormat="L"
  dateFormatCalendar="dddd"
  fixedHeight
  showMonthDropdown
  showYearDropdown
  dropdownMode="select"
  normalize={value => (value ? moment(value).format('YYYY-MM-DD') : null)}
  component={renderDatePicker}
/>

                  <Link to="/dashboard">
                    <Button
                        size="lg"
                        type="submit"
                        disabled={pristine || submitting} 
                        className="Requestbtn bg-gradient-Requestbtn btn-outline-info border-0"
                        block 
                        >
                            REQUEST ACCESS
                    </Button>
                   </Link>
              </Form>
            </Col>

    );
  }

RequestForm.propTypes = {
    usernameInputProps: PropTypes.object,
    roomnoInputProps: PropTypes.object,
    dobInputProps: PropTypes.object
}

RequestForm = reduxForm({
  form: 'request-form',
  validate,
})(RequestForm);

export default RequestForm;
