import React from 'react';
import { FormGroup,Input} from 'reactstrap';

const TextInput = ({ input,label, type, meta: { touched, error, warning } }) => (
  <div className={'form-group' + (touched && !{label} ? ' has-error' : '')}>
  <FormGroup>
      <Input {...input} placeholder={label} type={type} className="input-bgColor"/>
      {touched && ((error && <span className="help-block">{error}</span>) || (warning && <span>{warning}</span>))}
  </FormGroup>
}
                    </div>
);

export default TextInput;
