import React  from 'react';
import PropTypes from 'prop-types';
import {Col, Button, Form} from 'reactstrap';
import { Field, reduxForm } from 'redux-form';
import TextInput  from '../components/forms/TextInput';
// import {Link} from 'react-router-dom';
import DatePickerInput from './forms/DatePickerInput';
import moment from 'moment';

const validate = values => {
  const errors = {}
  if (!values.name) {
    errors.name = 'Please enter Name'
    } else if (!values.name.match(/^[a-zA-Z]*$/)) {
      errors.name = 'Please enter alphabet characters only.'
    } else if (values.name.length < 3) {
      errors.name = 'Minimum be 3 characters or more'
    }
  if (!values.roomno) {
    errors.roomno = 'Please enter your Room No'
  }
  if (!values.dob){
    errors.dob = 'Please select your date of birth'
  }
  return errors
  }

let RequestForm = props => {
    const { handleSubmit, pristine, submitting } = props
    return(
            <Col className="form-main">
              <Form onSubmit={handleSubmit} >

                    <Field component={TextInput} name="name" label="Name" />

                    <Field component={TextInput} name="roomno" label="Room No" />

                    {/* <Field component={TextInput} name="dob" label="Date of Birth" /> */}
                    <Field
                    name="dob"
                    placeholderText="Date of Birth"
                    inputValueFormat="YYYY-MM-DD"
                    fixedHeight
                    normalize={value => (value ? moment(value).format('YYYY-MM-DD') : null)}
                    component={DatePickerInput}
                    />

                  {/* <Link to="/dashboard"> */}
                    <Button
                        size="lg"
                        type="submit"
                        disabled={pristine || submitting} 
                        className="Requestbtn bg-gradient-Requestbtn btn-outline-info border-0"
                        block 
                        >
                            REQUEST ACCESS
                    </Button>
                   {/* </Link> */}
              </Form>
            </Col>

    );
  }

RequestForm.propTypes = {
  handleSubmit: PropTypes.string.isRequired,
};


export default reduxForm({
    form: 'guestForm',
    validate,
  })(RequestForm);
